import { useState } from "react";
import { Axios } from "axios";
const NewIntern = () => {
    const [companyName,setCompanyName] = useState("");
    const [jobRole,setJobRole] = useState("");
    const [stipend,setStipend] = useState(0);
    const [cutoff,setCutoff] = useState(0);
    
    const addToList = () =>{
        console.log("button working!!")
        Axios.post("http://localhost:3001/new-intern",{
            companyName,jobRole,stipend,cutoff
        })
    }

    console.log("New intern is accessed!")
    return (
        <div className="details">
            <form action="submit">
                <div>
                    <label for="CompanyName">Company Name</label>
                    <input type="text" id="CompanyName" onChange={(e)=>{
                        setCompanyName(e.target.value);
                    }}/>
                </div>
                <div>
                    <label for="JobRole">Job Role</label>
                    <input type="text" id="JobRole" onChange={(e)=>{
                        setJobRole(e.target.value);
                    }}/> 
                </div>
                <div>
                    <label for="Stipend">Stipend</label>
                    <input type="text" id="Stipend" onChange={(e)=>{
                        setStipend(e.target.value);
                    }}/> 
                </div>
                <div>
                    <label for="Cutoff">Cut-Off</label>
                    <input type="text" id="Cutoff" onChange={(e)=>{
                        setCutoff(e.target.value);
                    }}/> 
                </div>
                <button onClick={addToList}>Submit</button>
            </form>
        </div>
    );
}

export default NewIntern;
